	bool isNan(double n)
	{
		return (n != n);
	}

	int estimatePower(const Binary64& b64)
	{
		eclog::uint64_t significand = b64.significand();
		int exponent = b64.exponent();

		while ((significand & Binary64::hidden_bit) == 0)
		{
			significand = significand << 1;
			--exponent;
		}

		double log2 = 0.3010299956639812;
		double result = log2 * (Binary64::significand_size - 1 + exponent) - 1e-5;

		return (int)ceil(result);
	}

	int getPower(double d)
	{
		BigInteger numerator;
		BigInteger denominator;
		BigInteger deltaMinus;
		BigInteger deltaPlus;

		Binary64 b64(d);

		int power = estimatePower(b64);

		if (b64.exponent() > 0)
		{
			numerator.assign(b64.significand());
			numerator.multiplyPow2(b64.exponent());

			denominator.assign(1);
			denominator.multiplyPow10(power);

			numerator.shiftLeft(1);
			denominator.shiftLeft(1);

			deltaMinus.assign(1);
			deltaMinus.multiplyPow2(b64.exponent());

			deltaPlus.assign(1);
			deltaPlus.multiplyPow2(b64.exponent());
		}
		else if (power > 0)
		{
			numerator.assign(b64.significand());

			denominator.assign(1);
			denominator.multiplyPow5(power);
			denominator.multiplyPow2(-b64.exponent() + power);

			numerator.shiftLeft(1);
			denominator.shiftLeft(1);

			deltaMinus.assign(1);
			deltaPlus.assign(1);
		}
		else
		{
			numerator.assign(1);
			numerator.multiplyPow10(-power);

			deltaMinus.assign(numerator);
			deltaPlus.assign(numerator);

			numerator.multiply(b64.significand());

			denominator.assign(1);
			denominator.multiplyPow2(-b64.exponent());

			numerator.shiftLeft(1);
			denominator.shiftLeft(1);
		}

		if (b64.lowerBoundaryIsCloser())
		{
			numerator.shiftLeft(1);
			denominator.shiftLeft(1);
			deltaPlus.shiftLeft(1);
		}

		bool isEven = (b64.significand() & 1) == 0;

		bool inRange;

		if (isEven) {
			inRange = numerator.plusCompare(deltaPlus, denominator) >= 0;
		}
		else {
			inRange = numerator.plusCompare(deltaPlus, denominator) > 0;
		}

		if (!inRange)
		{
			numerator.multiply(10);
			deltaMinus.multiply(10);
			deltaPlus.multiply(10);

			power = power - 1;
		}

		return power;
	}

	int selectNotation(int power)
	{
		return (power >= -4 && power < 17) ? 1 : 0;
	}

	int getFracDigitCount(const char* s)
	{
		int count = 0;

		while (*s && *s != '.') {
			++s;
		}

		if (*s == '.')
		{
			while (isdigit(*++s)) {
				++count;
			}
		}

		return count;
	}

	double getTolerance(int n)
	{
		static const double t[] =
		{
			1e-323, 1e-322, 9.9e-322, 9.9e-321, 9.9e-320,
			9.9e-319, 9.900003e-318, 9.9e-317, 9.89999996e-316,
			9.9e-315, 9.9000000003e-314, 9.9e-313, 9.9e-312,
			9.9e-311, 9.9e-310
		};

		return (n < -308) ? t[n + 323] : 0.99 * pow(10.0, n);
	}

	void testFracDigits(double d, int fracDigits)
	{
		char buf[25];

		doubleToString(d, buf, fracDigits);

		CHECK(getFracDigitCount(buf) <= fracDigits);

		double dd = stringToDouble(buf);

		if (dd != d)
		{
			int power = getPower(d);

			int n = selectNotation(power) ? -fracDigits : power - fracDigits;

			double tolerance = getTolerance(n);

			double err = fabs(d - dd);

			CHECK(err <= tolerance);
		}
	}

	void testFracDigits(double d)
	{
		for (int p = 17; p >= 0; --p)
		{
			testFracDigits(d, p);
		}
	}
